mapper.update:
UPDATE qna
SET QNA_TITLE = #{QNA_TITLE},
QNA_CONTENT = #{QNA_CONTENT}
WHERE QNA_NO = #{QNA_NO}

mapper.select:
SELECT *
FROM (
SELECT qna.*, ROWNUM AS rnum
FROM (
SELECT * FROM qna

    WHERE qna_title like '%${keyword}%'

ORDER BY QNA_NO DESC
) qna
)
WHERE rnum BETWEEN #{startIndex} AND #{endIndex}
